import{_ as i,X as l,Y as a,a1 as e,a0 as r}from"./framework-68dd73a2.js";const n={},t=r('<h1 id="川菜经典黄豆炖猪蹄详细做法" tabindex="-1"><a class="header-anchor" href="#川菜经典黄豆炖猪蹄详细做法" aria-hidden="true">#</a> 川菜经典黄豆炖猪蹄详细做法</h1><div class="hint-container warning"><p class="hint-container-title">注意</p><p>本博文仅供学术研究和交流参考，严禁将其用于商业用途。如因违规使用产生的任何法律问题，使用者需自行负责。</p></div><h3 id="材料" tabindex="-1"><a class="header-anchor" href="#材料" aria-hidden="true">#</a> 材料</h3><ol><li>猪蹄</li><li>黄豆</li></ol><h3 id="佐料" tabindex="-1"><a class="header-anchor" href="#佐料" aria-hidden="true">#</a> 佐料</h3><ol><li>姜</li><li>大葱</li><li>冰糖</li><li>八角</li><li>香叶</li><li>干辣椒</li><li>生抽</li><li>老抽</li><li>豆瓣酱</li><li>啤酒</li><li>盐</li><li>鸡精/味精</li></ol><h3 id="工具" tabindex="-1"><a class="header-anchor" href="#工具" aria-hidden="true">#</a> 工具</h3><ol><li>电炒锅一套</li><li>铲子</li><li>洗菜篮</li></ol><h3 id="做法" tabindex="-1"><a class="header-anchor" href="#做法" aria-hidden="true">#</a> 做法</h3><ol><li>剁好的猪蹄放入淡盐水中浸泡十分钟</li><li>再用清水清洗干净</li><li>然后冷水下锅</li><li>加入姜葱料酒煮出血沫</li><li>用热水再次清洗</li><li>沥干水分之后下锅煸炒出油脂</li><li>而后加入葱姜八角香叶干辣椒生抽老抽冰糖豆瓣酱炒至上色</li><li>倒入泡好的黄豆(<strong>黄豆应该提前两小时泡好</strong>)</li><li>加入一瓶啤酒</li><li>再加入适量热水</li><li>再放入盐、味精/鸡精 调味</li><li>扣盖闷至少一小时而后即可出锅</li></ol><hr><h3 id="分割线" tabindex="-1"><a class="header-anchor" href="#分割线" aria-hidden="true">#</a> 分割线</h3><hr><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>以上就是我关于 <em>川菜经典黄豆炖猪蹄详细做法</em> 知识点的整理与总结的全部内容，希望对你有帮助。。。。。。。</strong></p></div>',14);function h(d,o){return l(),a("div",null,[e(" more "),t])}const c=i(n,[["render",h],["__file","food15.html.vue"]]);export{c as default};
