import{_ as i,X as l,Y as a,a1 as e,a0 as r}from"./framework-68dd73a2.js";const n={},h=r('<h1 id="川菜经典小炒肉的详细做法" tabindex="-1"><a class="header-anchor" href="#川菜经典小炒肉的详细做法" aria-hidden="true">#</a> 川菜经典小炒肉的详细做法</h1><div class="hint-container warning"><p class="hint-container-title">注意</p><p>本博文仅供学术研究和交流参考，严禁将其用于商业用途。如因违规使用产生的任何法律问题，使用者需自行负责。</p></div><h3 id="材料" tabindex="-1"><a class="header-anchor" href="#材料" aria-hidden="true">#</a> 材料</h3><ol><li>螺丝椒(二荆条)</li><li>五花肉</li></ol><h3 id="佐料" tabindex="-1"><a class="header-anchor" href="#佐料" aria-hidden="true">#</a> 佐料</h3><ol><li>食用盐</li><li>食用油</li><li>小米椒</li><li>大蒜</li><li>生抽</li><li>老抽</li><li>豆豉</li><li>鸡精/味精</li><li>白糖</li></ol><h3 id="工具" tabindex="-1"><a class="header-anchor" href="#工具" aria-hidden="true">#</a> 工具</h3><ol><li>电炒锅全套</li><li>铲子</li><li>菜板</li><li>菜刀</li><li>洗菜篮</li><li>碗/盘</li></ol><h3 id="做法" tabindex="-1"><a class="header-anchor" href="#做法" aria-hidden="true">#</a> 做法</h3><ol><li>将螺丝椒切以及小米辣45度切成长条状并洗净</li><li>五花肉去皮切片洗净备用(可用沸水去除浮末或去腥)</li><li>锅中烧热将切好的辣椒放入加入少许的食用盐开小火边炒边用铲子按压，炒断生后倒出备用</li><li>锅中放入少量的油加热后放入五花肉炒干水分</li><li>加入大蒜、豆豉、老抽 翻炒</li><li>加入辣椒翻炒</li><li>加入鸡精、白糖、生抽翻炒均匀之后即可出锅</li></ol><hr><h3 id="分割线" tabindex="-1"><a class="header-anchor" href="#分割线" aria-hidden="true">#</a> 分割线</h3><hr><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>以上就是我关于 <em>川菜经典小炒肉的详细做法</em> 知识点的整理与总结的全部内容，希望对你有帮助。。。。。。。</strong></p></div>',14);function t(d,o){return l(),a("div",null,[e(" more "),h])}const s=i(n,[["render",t],["__file","food21.html.vue"]]);export{s as default};
