import{_ as i,X as l,Y as a,a1 as e,a0 as r}from"./framework-68dd73a2.js";const n={},h=r('<h1 id="川菜经典凉面-营山-详细做法" tabindex="-1"><a class="header-anchor" href="#川菜经典凉面-营山-详细做法" aria-hidden="true">#</a> 川菜经典凉面(营山)详细做法</h1><div class="hint-container warning"><p class="hint-container-title">注意</p><p>本博文仅供学术研究和交流参考，严禁将其用于商业用途。如因违规使用产生的任何法律问题，使用者需自行负责。</p></div><h3 id="材料" tabindex="-1"><a class="header-anchor" href="#材料" aria-hidden="true">#</a> 材料</h3><ol><li>碱水面</li><li>豆芽</li></ol><h3 id="佐料" tabindex="-1"><a class="header-anchor" href="#佐料" aria-hidden="true">#</a> 佐料</h3><ol><li>姜</li><li>蒜</li><li>生抽</li><li>陈醋</li><li>花椒面</li><li>食用盐</li><li>白糖</li><li>鸡精/味精</li><li>红油辣椒</li><li>菜籽油</li><li>芽菜</li><li>花生碎</li><li>葱花</li></ol><h3 id="工具" tabindex="-1"><a class="header-anchor" href="#工具" aria-hidden="true">#</a> 工具</h3><ol><li>电炒锅全套</li><li>洗菜篮</li><li>碗/盘子</li><li>打蒜的机器</li></ol><h3 id="做法" tabindex="-1"><a class="header-anchor" href="#做法" aria-hidden="true">#</a> 做法</h3><ol><li>将水煮沸后加入面条，而后进行三煮三沸(水煮沸十秒后加入冷水如此反复三次)</li><li>面条起锅前再加入一大盆冷水</li><li>捞出来放入篮子里沥干水份</li><li>加入菜籽油并搅拌均匀</li><li>将豆芽放入沸水中烫熟后沥干水份后放入盘子</li><li>将凉面放入盘中</li><li>淋上料汁(生抽、陈醋、花椒面、盐、白糖、鸡精、姜蒜水、红油辣椒)</li><li>撒上芽菜花生碎和葱花即可</li></ol><hr><h3 id="分割线" tabindex="-1"><a class="header-anchor" href="#分割线" aria-hidden="true">#</a> 分割线</h3><hr><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>以上就是我关于 <em>川菜经典凉面(营山)详细做法</em> 知识点的整理与总结的全部内容，希望对你有帮助。。。。。。。</strong></p></div>',14);function t(d,o){return l(),a("div",null,[e(" more "),h])}const s=i(n,[["render",t],["__file","food23.html.vue"]]);export{s as default};
