import{_ as i,X as a,Y as e,a1 as l,a0 as r}from"./framework-68dd73a2.js";const n={},t=r('<h1 id="川菜经典炸酥肉详细做法" tabindex="-1"><a class="header-anchor" href="#川菜经典炸酥肉详细做法" aria-hidden="true">#</a> 川菜经典炸酥肉详细做法</h1><div class="hint-container warning"><p class="hint-container-title">注意</p><p>本博文仅供学术研究和交流参考，严禁将其用于商业用途。如因违规使用产生的任何法律问题，使用者需自行负责。</p></div><h3 id="材料" tabindex="-1"><a class="header-anchor" href="#材料" aria-hidden="true">#</a> 材料</h3><ol><li>夹子肉</li><li>红薯粉</li></ol><h3 id="佐料" tabindex="-1"><a class="header-anchor" href="#佐料" aria-hidden="true">#</a> 佐料</h3><ol><li>菜籽油</li><li>食用盐</li><li>鸡精</li><li>花椒面</li><li>料酒</li><li>鸡蛋</li><li>姜粒</li></ol><h3 id="工具" tabindex="-1"><a class="header-anchor" href="#工具" aria-hidden="true">#</a> 工具</h3><ol><li>电炒锅全套</li><li>大小漏勺</li><li>菜刀</li><li>菜板</li></ol><h3 id="做法" tabindex="-1"><a class="header-anchor" href="#做法" aria-hidden="true">#</a> 做法</h3><ol><li>将夹子肉洗净后切成小小的溥片(肥廋分开切后将其混合)</li><li>放入食用盐、鸡精、花椒面、料酒、鸡蛋、姜粒混合均匀</li><li>肉调好后放入适量的红薯芡粉，比例为10:6肉为10</li><li>将锅中菜籽油烧烧熟后降至五成热，把肉一坨一坨的放进去，全程用小火慢慢的炸</li><li>炸至金黄色捞出以备二次复炸</li><li>待锅中油温升高后将炸过的酥(sgty)肉第二次下锅油炸，中火轻轻的油炸一两分钟即可捞出食用</li></ol><hr><h3 id="分割线" tabindex="-1"><a class="header-anchor" href="#分割线" aria-hidden="true">#</a> 分割线</h3><hr><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>以上就是我关于 <em>川菜经典炸酥肉详细做法</em> 知识点的整理与总结的全部内容，希望对你有帮助。。。。。。。</strong></p></div>',14);function h(d,o){return a(),e("div",null,[l(" more "),t])}const s=i(n,[["render",h],["__file","food29.html.vue"]]);export{s as default};
