import{_ as i,X as l,Y as a,a1 as e,a0 as r}from"./framework-68dd73a2.js";const n={},h=r('<h1 id="川菜经典红烧牛肉面详细做法" tabindex="-1"><a class="header-anchor" href="#川菜经典红烧牛肉面详细做法" aria-hidden="true">#</a> 川菜经典红烧牛肉面详细做法</h1><div class="hint-container warning"><p class="hint-container-title">注意</p><p>本博文仅供学术研究和交流参考，严禁将其用于商业用途。如因违规使用产生的任何法律问题，使用者需自行负责。</p></div><h3 id="材料" tabindex="-1"><a class="header-anchor" href="#材料" aria-hidden="true">#</a> 材料</h3><ol><li>牛肉</li><li>面条</li><li>青菜(上海青/空心菜)</li></ol><h3 id="佐料" tabindex="-1"><a class="header-anchor" href="#佐料" aria-hidden="true">#</a> 佐料</h3><ol><li>食用油</li><li>葱</li><li>姜</li><li>料酒</li><li>豆瓣酱</li><li>生抽</li><li>老抽</li><li>冰糖</li><li>八角</li><li>桂皮</li><li>香叶</li><li>食用盐</li><li>鸡精/味精</li></ol><h3 id="工具" tabindex="-1"><a class="header-anchor" href="#工具" aria-hidden="true">#</a> 工具</h3><ol><li>电炒锅全套</li><li>铲子</li><li>洗菜篮</li><li>菜刀</li><li>菜板</li><li>碗/盘</li></ol><h3 id="做法" tabindex="-1"><a class="header-anchor" href="#做法" aria-hidden="true">#</a> 做法</h3><ol><li>剁好的牛肉冷水下锅,放葱姜料酒煮出血沫，并去除</li><li>用热水清洗刚煮好的牛肉</li><li>洗干后下锅煸炒(可不放油)，炒至焦黄</li><li>放一勺豆瓣酱炒出红油</li><li>加一勺生抽一勺老抽8粒冰糖葱姜八角桂皮香叶</li><li>一次性加够热水，开大火煮开中小火炖九十分钟</li><li>放少许盐鸡精，牛肉汤算是做好了</li><li>将面条用清水煮好，可以加点青菜(上海青/空心菜)</li><li>将面条捞出而后浇上牛肉汤和牛肉即可</li></ol><hr><h3 id="分割线" tabindex="-1"><a class="header-anchor" href="#分割线" aria-hidden="true">#</a> 分割线</h3><hr><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>以上就是我关于 <em>川菜经典红烧牛肉面详细做法</em> 知识点的整理与总结的全部内容，希望对你有帮助。。。。。。。</strong></p></div>',14);function t(d,o){return l(),a("div",null,[e(" more "),h])}const s=i(n,[["render",t],["__file","food31.html.vue"]]);export{s as default};
