import{_ as a,X as r,Y as n,a1 as i,Z as t,$ as d,a2 as l,a0 as o,F as s}from"./framework-68dd73a2.js";const c={},h={id:"_161-java-cs模式与bs模式的区别-md",tabindex:"-1"},_=t("a",{class:"header-anchor",href:"#_161-java-cs模式与bs模式的区别-md","aria-hidden":"true"},"#",-1),p={href:"http://xn--161-Java-CSBS-650ux29b44det0eda5594cea6077d.md",target:"_blank",rel:"noopener noreferrer"},m=o('<div class="hint-container warning"><p class="hint-container-title">注意</p><p>本博文仅供学术研究和交流参考，严禁将其用于商业用途。如因违规使用产生的任何法律问题，使用者需自行负责。</p></div><ul><li><p>常见的C/S模式：</p><ul><li>数据库服务器</li><li>网络游戏客户端/服务器端</li><li>QQ客户端/QQ服务器端</li></ul></li><li><p>B/S模式</p><ul><li>主要应用于广域网</li><li>软件业务逻辑大部分在服务器端实现，不必开发客户端，应用广泛，易于管理维护</li><li>用户界面完全通过浏览器实现，一部分事务逻辑在前端实现，但是主要事务逻辑在服务器端实现</li></ul></li><li><p>C/S模式与B/S模式的比较</p></li></ul><table><thead><tr><th>比较</th><th>C/S</th><th>B/S</th></tr></thead><tbody><tr><td>硬件</td><td>小范围，固定</td><td>可以无专门硬件</td></tr><tr><td>安全</td><td>用户固定，强</td><td>用户不可知，较弱</td></tr><tr><td>程序架构</td><td>注重流程</td><td>注重安全及速度</td></tr><tr><td>软件重用</td><td>复杂，不方便</td><td>构件独立，便于重用</td></tr><tr><td>维护</td><td>两端维护</td><td>服务器个别构件维护</td></tr><tr><td>用户接口</td><td>以Window平台为基础， 接口有限</td><td>建立在浏览器之上，表 达丰富</td></tr><tr><td>信息流</td><td>中央集权，交互性低</td><td>流向可变，类似交易中心</td></tr></tbody></table><hr><h3 id="分割线" tabindex="-1"><a class="header-anchor" href="#分割线" aria-hidden="true">#</a> 分割线</h3><hr><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>以上就是我关于 <em>Java-CS模式与BS模式的区别</em> 知识点整理与总结的全部内容，希望对你有帮助。。。。。。。</strong></p></div>',7);function u(v,S){const e=s("ExternalLinkIcon");return r(),n("div",null,[i(" more "),t("h1",h,[_,d(),t("a",p,[d("161-Java-CS模式与BS模式的区别.md"),l(e)])]),m])}const b=a(c,[["render",u],["__file","java161.html.vue"]]);export{b as default};
